<?php

namespace AppBundle\Repository;

use AppBundle\Entity\Location;
use AppBundle\Utils\Sorter;
use Pagerfanta\Pagerfanta;

/**
 * LocationRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class LocationRepository extends \Doctrine\ORM\EntityRepository
{
    const NUM_ITEMS = 20;

    public function findLocationByParent(string $location, $parent): ?Location
    {
        return $this->createQueryBuilder('p')
            ->where('j.id = :parent')
            ->andWhere('p.name LIKE :name')
            ->join('p.parent', 'j')
            ->setParameter('name', $location)
            ->setParameter('parent', ($parent) ? $parent->getId() : false)
            ->getQuery()
            ->useQueryCache(true)
            ->useResultCache(true)
            ->getOneOrNullResult();
    }

    public function findAllByCountry(Country $country, int $page) :Pagerfanta
    {
        $query = $this->createQueryBuilder('p')
            ->where('p.parent = false')
            ->andWhere('p.isEnabled =  true')
            ->andWhere('p.country = :country')
            ->setParameter('country', $country)
            ->orderBy('p.title', 'ASC')
            ->getQuery()
            ->useQueryCache(true)
            ->useResultCache(true);

        return Sorter::createPaginator($query, $page, self::NUM_ITEMS);
    }

    public function findAllByParent(Location $parent, int $page) :Pagerfanta
    {
        $query = $this->createQueryBuilder('p')
            ->where('p.isEnabled = true')
            ->andWhere('p.parent = :parent')
            ->setParameter('slug', $parent)
            ->orderBy('p.title', 'ASC')
            ->getQuery()
            ->useQueryCache(true)
            ->useResultCache(true);

        return Sorter::createPaginator($query, $page, self::NUM_ITEMS);
    }

    public function findBySlug(string $slug) : ?Location
    {
        return $this->createQueryBuilder('p')
                ->andWhere('p.isEnabled = true')
                ->andWhere('p.slug = :slug')
                ->setParameter('slug', $slug)
                ->getQuery()
                ->useQueryCache(true)
                ->useResultCache(true)
                ->getOneOrNullResult();
    }
}
